INSERT INTO "Scientists" (id,first_name,last_name,patronymic,biography,birth_date,death_date) VALUES (1, 'Владимир', 'Перцев', 'Николаевич', 'bio_undefined', '1877-06-15', '1960-06-03');
INSERT INTO "ScientistPhotos" (id,title,image_path,scientist_id) VALUES (1, 'Перцев В.Н', 'Основная.jpg', 1);
INSERT INTO "MemoryPlaces" (id,ordinal_number,name,description,coordinates_id,scientist_id) VALUES (1, 1, 'Курское реальное училище (сегодня Профессиональный лицей №1)', '<p>Владимир Николаевич Перцев родился 15 июля 1877 г. в Курске в дворянской семье. Его отец, Николай Никанорович, в чине статского советника состоял членом попечительского совета женской прогимназии и считал, что сын должен избрать техническую специальность. В 1888-1895 гг. Владимир Перцев по настоянию отца прошел полный курс в Курском реальном училище. Смерть отца от туберкулеза в 1890 г. серьезно осложнило материальное положение будущего ученого.</p>', 2001, 1);				
INSERT INTO "MemoryPlaces" (id,ordinal_number,name,description,coordinates_id,scientist_id) VALUES (2, 2, 'г. Харьков', '<p>Однако молодого человека увлекали гуманитарные науки, в особенности история. Но чтобы поступить в университет, следовало сдать экзамены за курс гимназии и выучить классические языки – латинский и греческий. После непродолжительной подготовки в 1897 г. юноша едет в Харьков и там при 2-й гимназии сдает экзамены на аттестат зрелости. Вот что он по этому поводу писал в своей официальной автобиографии: «Меня привлекали к себе гуманитарные науки, но доступ в университеты в то время для окончивших реальное училище был закрыт. Я стал готовиться к экзамену экстерном за курс гимназии, пока же поступил сначала в Петербургский технологический институт, потом в Варшавский ветеринарный институт.</p>', 2002, 1);				
INSERT INTO "MemoryPlaces" (id,ordinal_number,name,description,coordinates_id,scientist_id) VALUES (3, 3, 'Императорский Московский университет', '<p>В 1897 г. Владимир Николаевич поступил на историко-филологический факультет Московского университета. Во время учебы в университете В. Н. Перцев занимался в семинарах известных ученых – Р. Ю. Виппера, П. Г. Виноградова, М. С. Карелина. Увлекся политикой и активно участвовал в студенческих выступлениях. Об этом он писал в автобиографии: «За участие в студенческих волнениях я два раза подвергался исключению из университета (в 1899 и 1901 гг.), причем после короткого (2-недельного) заключения в Бутырской тюрьме каждый раз подвергался высылке из Москвы в г. Курск под надзор полиции. Одно время я потерял даже надежду окончить университетский курс в России и в 1901 г. на собранные уроками деньги, с частичной помощью моей матери (отец умер в 1890 г.) совершил поездку за границу. Я слушал лекции в Венском и Берлинском университетах, но недостаток средств побудил меня вернуться в Россию, где я был снова принят в университет». В этом высшем учебном заведении Владимир провел с перерывами шесть лет, окончив его в 1903 г. В. П. Перцев получил диплом первой степени и был оставлен для подготовки к профессорскому званию на кафедре всеобщей истории у профессора Р. Ю. Виппера. Стипендия была невелика, и приходилось сочетать занятия наукой на кафедре с преподаванием истории в Московской женской гимназии. Однако преподавательская деятельность В. Н. Перцева вскоре была прервана: в конце 1903 г. московский градоначальник не выдал ему свидетельства о политической благонадежности, ставя в вину участие в студенческих волнениях. Только в 1905 г., когда началась первая русская революция, Владимир Николаевич устроился на преподавательскую работу в Московское коммерческое училище.</p>', 2003, 1);				
INSERT INTO "MemoryPlaces" (id,ordinal_number,name,description,coordinates_id,scientist_id) VALUES (4, 4, 'Преподавательская деятельность', '<p>После Октябрьской революции В.Н. Перцев продолжил свою научную и педагогическую деятельность, но уже в Советском государстве, которое приступило к строительству советской высшей школы. Особая забота проявлялась об организации высших учебных заведений на национальных окраинах Советского государства. В 1918 г. вместе с В. И. Пичетой, Н. М. Никольским, В. Н. Дьяковым и другими учеными Владимир Николаевич выехал в Западную область, где вошел в состав Ученого совета Смоленского государственного университета. 7 ноября 1918 г. был избран профессором кафедры истории Германии, и читал лекции по империализму, истории Греции и истории Германии. Одновременно он работал в Смоленском пединституте, позднее преобразованном в Институт народного образования, преподавал в Московском пединституте, в Витебском институте народного образования, в партийной школе при Оршанском Пролетарском университете и на курсах для школьных работников в Городке Витебской губернии. Несомненно, такая активная преподавательская деятельность объяснялась еще и тяжелыми материальными условиями тех лет. Тогда же началась и активная научно-издательская деятельность: В. Н. Перцев стал публиковать научные статьи, популярные очерки, учебные пособия для школьников, рецензии; к 1917 г. имел около полутора сотен публикаций.</p>', 2004, 1);				
INSERT INTO "MemoryPlaces" (id,ordinal_number,name,description,coordinates_id,scientist_id) VALUES (5, 5, 'БГУ', '<p>Летом 1921 г. В. Н. Перцев принимал активное участие в подготовке к открытию Белорусского государственного университета, а 1 октября 1921 г. был утвержден профессором по кафедре всеобщей истории (одновременно был профессором кафедры народного хозяйства БГУ). Впервые Владимир Николаевич приехал в Минск в октябре 1921 г., когда университет готовился к открытию. <br/>В первый год работы БГУ профессор преподавал на факультете общественных наук, читал курсы по истории Нового времени на разных отделениях. Тогда он еще оставался профессором Смоленского университета и приезжал в Минск лишь для чтения лекций. Но уже весной 1922 г. вместе с профессором Н. М. Никольским В. Н. Перцев обратился в Правление БГУ с просьбой поспособствовать переезду семьи в Минск на постоянное место жительства. Переезд состоялся в июне 1922 г. <br/>В конце первого учебного года деканатом ФОНа был поднят вопрос об организации при БГУ показательной школы. На специальном заседании деканата 22 июля 1922 г. с докладом по этому вопросу выступил В. Н. Перцев. Благодаря его обоснованиям было принято решение назначить временный президиум школы из трех человек. Заведующим педагогической частью избрали Владимира Николаевича. Эту инициативу впоследствии поддержало руководство педагогического факультета, и при БГУ многие годы действовали две показательные школы (белорусская и еврейская). <br/>Летом 1922 г., после выделения из состава ФОНа педагогического факультета, В. Н. Перцев стал преподавать на двух факультетах. На ФОНе он читал курсы Новой истории и истории народного хозяйства Западной Европы. На педфаке его основными курсами были история Древней Греции (после увольнения профессора Д. П. Кончаловского) и история Европы XIX в. Вел он также просеминарий по истории Западной Европы и семинарий по крестьянскому вопросу. Позднее стал вести семинарий по истории Французской революции. Знания профессора были востребованы и на рабочем факультете. Регулярно Владимир Николаевич выступал с докладами и речами, приуроченными к торжественным и праздничным датам. Так, в 1924 и 1926 гг. он сделал доклады на заседаниях, посвященных Парижской коммуне, а в декабре 1926 г. – на собрании памяти Михаила Бакунина.<br/>Наряду с преподаванием в БГУ у столь востребованного историка много времени занимала научно-просветительская деятельность среди рабочей и солдатской аудиторий. Этой молодежи, так тянувшейся к знаниям, интересны были чуть ли не все темы, которые излагал компетентный ученый. Например, публичная лекция на тему «Аграрный вопрос на Западе», прочитанная 16 января 1927 г., собрала огромную аудиторию, едва вместившуюся в актовом зале университета. <br/>В Минске В. Н. Перцев не оставлял свои научные изыскания. Он публиковался в «Трудах БГУ», юбилейном сборнике, посвященном 400-летию книгопечатания в Беларуси, в 1924 г. издал монографию «Экономическое развитие Англии в XIX в.», которая восполнила отсутствие специальной литературы по данной теме в русскоязычной историографии.<br/>В 1928 г. на педфаке была создана объединенная кафедра всеобщей истории с четырьмя отделениями, и В. Н. Перцев стал на ней профессором.</p>', 2005, 1);				
INSERT INTO "MemoryPlaces" (id,ordinal_number,name,description,coordinates_id,scientist_id) VALUES (6, 6, 'г. Витебск', '<p>В 1922 г. Владимиру Николаевичу поручили важную миссию – принять имущество Витебского, а также Смоленского отделений Московского археологического института, которое решением центральных властей передавалось БГУ. В отделениях хранились богатые фонды литературы и исторических источников. Перцев обнаружил в Витебске библиотеку и музей. 8 тысяч томов библиотеки содержали в себе в основном книги на латыни и польском языке из иезуитских коллегиумов, рукописи по истории противостояния православия и католицизма и в целом по истории Беларуси. В музее хранились в основном иконы старого письма, а также находки археологических и этнографических экспедиций по Беларуси. В течение октября, ноября и декабря велись долгие бюрократические переписки. Во время командировок в г. Витебск он столкнулся с явным нежеланием местных чиновников делиться имуществом, и спорный вопрос дошел до Совнаркома Беларуси. В итоге имущество все-таки было передано БГУ, и в благополучном разрешении вопроса Правление университета отметило личную заслугу В. Н. Перцева.</p>', 2006, 1);				
INSERT INTO "MemoryPlaces" (id,ordinal_number,name,description,coordinates_id,scientist_id) VALUES (7, 7, 'Научные командировки', '<p>Правление университета всячески стремилось помогать профессорам в их научной работе. В первую очередь изыскивало средства на оплату научных командировок. Так, весной 1924 г. было удовлетворено ходатайство В. Н. Перцева об оплате суточных и проезда в Москву на время весенних каникул. В 1929 г. он получил финансирование для научной командировки в Киев. Весной 1928 г. в план было включено заявление В. Н. Перцева о необходимости изучить документы и литературу в Германии по аграрной истории этой страны в XVIII в. Согласие на командировку дали, но «без выдачи средств», что в итоге не позволило профессору выехать за рубеж.</p>', 2007, 1);				
INSERT INTO "MemoryPlaces" (id,ordinal_number,name,description,coordinates_id,scientist_id) VALUES (8, 8, 'Высший педагогический институт', '<p>В 1931 г. в связи с «разукрупнением» БГУ Владимир Николаевич, как и многие другие историки, был переведен на работу в Высший педагогический институт, где его назначили заведующим кафедрой истории Запада (с 1934 г. – кафедра всеобщей истории). Сотрудником педагогического института В. Н. Перцев являлся с перерывами до 1947 г., совмещая заведование кафедрой с работой в АН БССР и БГУ.</p>', 2008, 1);				
INSERT INTO "MemoryPlaces" (id,ordinal_number,name,description,coordinates_id,scientist_id) VALUES (9, 9, 'Возвращение в БГУ',  '<p>В 1934 г., после создания исторического факультета, Владимир Николаевич стал профессором кафедры общей истории. В 1935 г. по совокупности научных публикаций решением квалификационной комиссии Наркомпроса БССР ему было присуждено звание доктора исторических наук. Общим курсом, который он читал на новом факультете, была «История Средних веков», но профессор предложил студентам спецкурс по истории Пруссии и спецсеминар «Ранний буржуазный гуманизм». В первые годы существования исторического факультета БГУ Владимир Николаевич работал на нем по совместительству, так как основным местом работы был пединститут. Но руководство университета пошло на необычный шаг, описанный в пояснительной записке к штатному формуляру на 1937/38 учебный год: «Профессор Перцев В. Н. зачислен штатным работником по Белорусскому Государственному Университету, несмотря на то, что он зачислен штатным работником и по Минскому пединституту; это объясняется тем, что в БГУ нагрузка по древней и средней истории очень большая (600 профессорских часов в год), а другого работника по этим предметам у нас нет». Столь ценному и незаменимому специалисту был установлен повышенный персональный оклад. С 1936 г. историк числится профессором кафедры истории Древнего мира и Средних веков, которой заведовал академик Н. М. Никольский. Но в 1939 г. Владимир Николаевич возглавил отдельную кафедру истории Средних веков, возникшую в результате ее раздела на две самостоятельные. Состав кафедры был весьма скромен – сам В. Н. Перцев, его бывший аспирант и уже заместитель декана истфака А. И. Партон, а также два аспиранта – Я. С. Зингер и Р. З. Полуйко. Владимир Николаевич был активным членом Совета БГУ.</p>', 2009, 1);				
INSERT INTO "MemoryPlaces" (id,ordinal_number,name,description,coordinates_id,scientist_id) VALUES (10, 10, 'Большая Советская Энциклопедия (здание издательства располагалось на этом месте)', '<p>Тридцатые годы в жизни В. Н. Перцева заполнены, кроме преподавания в университете и педагогическом институте, подготовкой материалов для Энциклопедического словаря Гранат и первого издания Большой Советской Энциклопедии. Тут в полной мере раскрылись широта интересов и глубина знаний историка. Около 220 статей он написал для словаря Граната и Большой советской энциклопедии.</p>', 2010, 1);				
INSERT INTO "MemoryPlaces" (id,ordinal_number,name,description,coordinates_id,scientist_id) VALUES (11, 11, '"взять на карандаш"', '<p>В 1939 г. общественность БГУ и МПИ отметила 35-летие научно-педагогической деятельности Владимира Николаевича Перцева. К этому юбилею планировалось приурочить и награждение профессора государственной наградой. Однако в дело вмешались органы нацбезопасности. В докладной записке Л. Ф. Цанавы от 2 июня 1939 г. на имя Первого секретаря ЦК КП(б)Б П. К. Пономаренко было отмечено, что В. Н. Перцев настроен антисоветски, ведет контрреволюционную пропаганду, в преподавании делает акцент на историю Германии. Ему также припомнили увлечение партией кадетов в дореволюционное время. В итоге награду тогда так и не вручили. Это был не единственный случай пристального внимания органов к уважаемому профессору. Еще в 1932 г. он проходил по документам ОГПУ как «антисоветский элемент». В его характеристике, написанной не позднее 1937 г., отмечены такого рода «недостатки»: «...марксистской методологией владеет недостаточно. Отсюда в области обобщений т. Перцев не дает того, что требуется от историка-марксиста. В общественной жизни принимает недостаточное участие». Понятно, что такие отзывы вынуждали Владимира Николаевича оправдываться: «Статьи и книги, написанные в основном до 1930 г., не всегда и не во всем удовлетворяли требованиям марксистско-ленинской методологии». Но стоит отметить, что и в следующих изданиях своих работ он продолжал придерживаться тех самых взглядов, за которые его критиковали идеологи.</p>', 2011, 1);				
INSERT INTO "MemoryPlaces" (id,ordinal_number,name,description,coordinates_id,scientist_id) VALUES (12, 12, 'Курский педагогический институт', '<p>24 июня 1941 г. В. Н. Перцев эвакуировался из Минска в Курск. До октября работал профессором в Курском педагогическом институте. Осенью 1941 г. в связи с приближением немецко-фашистских войск к г. Курску институт был эвакуирован в г. Сарапул Удмуртской АССР. Летом 1943 г. учебное здание педагогического института было взорвано и сожжено.</p>', 2012, 1);				
INSERT INTO "MemoryPlaces" (id,ordinal_number,name,description,coordinates_id,scientist_id) VALUES (13, 13, 'Удмуртский государственный педагогический институт', '<p>С началом Великой Отечественной войны Владимир Николаевич успел эвакуироваться из Минска в Курск. До октября работал профессором в Курском педагогическом институте. В октябре переехал в г. Сарапул Удмуртской АССР, а затем в г. Ижевск. На протяжении почти двух лет (с 15 декабря 1941 г. по 1 сентября 1943 г.) он работал профессором и заведующим кафедрой всеобщей истории Удмуртского государственного педагогического института, читал лекции, выступал с публикациями в местной прессе, с докладами в воинских частях и госпиталях.</p>', 2013, 1);				
INSERT INTO "MemoryPlaces" (id,ordinal_number,name,description,coordinates_id,scientist_id) VALUES (14, 14, 'г. Казань', '<p>В. Н. Перцев принял участие и в работе сессии АН БССР, состоявшейся в марте 1942 г. в Казани, выступив с докладом «Фашизм и средневековье». В своем выступлении Владимир Николаевич убедительно показал общее и особенное в идеологии средневековья и фашистской диктатуры так называемого «тотального» государства, раскрыл те моменты средневековой истории, которые были взяты на вооружение фашистскими идеологами и служили им для обоснования своих теорий. По его словам, фашистских теоретиков в средневековой истории привлекали идеи вождизма, право силы и террора, волюнтаризм, мистика, аскетизм, господство аристократического меньшинства над массами, антисемитизм и т. п. Однако, подчеркивал академик Перцев, при всех симпатиях фашистских идеологов к средним векам и при всей близости средневековой идеологии к идеологии фашистской, … проводить знак равенства между этими двумя мировоззрениями, возникшими при совершенно иных социальных и политических условиях, никак нельзя. По его мнению, откровенно выражаемое фашистами сочувствие средневековью – это только прикрытие и средство, с помощью которых они стремятся феодальные идеи, а иногда и феодальные порядки, и методы управления приспособить к потребностям … капитализма.</p>', 2014, 1);				
INSERT INTO "MemoryPlaces" (id,ordinal_number,name,description,coordinates_id,scientist_id) VALUES (15, 15, 'г. Ташкент', '<p>В ноябре 1942 г сессии АН БССР проходила в г. Ташкент. Владимир Николаевич выступал с докладом «Пруссия до завоевания ее немцами». Это был первый шаг к созданию монументального труда по истории Пруссии, которому ученый посвятил многие годы исследований.</p>', 2015, 1);				
INSERT INTO "MemoryPlaces" (id,ordinal_number,name,description,coordinates_id,scientist_id) VALUES (16, 16, 'Железнодорожная станция Сходня', '<p>В сентябре 1943 г. В. Н. Перцев оставил работу в Ижевске и переехал в Подмосковье, где на ст. Сходне возобновил свою работу БГУ. Тут Владимир Николаевич вновь возглавил кафедру истории Средних веков, а с декабря 1943 г. приступил к исполнению обязанностей академика-секретаря Отделения общественных наук АН БССР</p>', 2016, 1);				
INSERT INTO "MemoryPlaces" (id,ordinal_number,name,description,coordinates_id,scientist_id) VALUES (17, 17, 'Национальная академия наук Беларуси', '<p>11 апреля 1940 г. Владимир Николаевич был избран действительным членом Академии наук БССР. К 1941 г. В. Н. Перцев стал одной из наиболее знаковых личностей всего университета и, безусловно, ведущим научным сотрудником Академии наук. В суровых военных условиях марта 1942 г. состоялась сессия АН БССР, в которой участвовал и В. Н. Перцев. В своем выступлении он объявил, что начинает разрабатывать новую тему – «Захватническая политика немцев на Востоке в XIII–XV столетиях». Новое научное направление было вызвано политической ситуацией, войной и определялось повышенным интересом к германской истории. Таким образом, специалист по немецкой (прусской) истории был востребован во всех отношениях. Уже на следующей сессии АН БССР, которая проходила в Ташкенте в ноябре 1942 г., Владимир Николаевич выступил с докладом «Пруссия до завоевания ее немцами». Это был первый шаг к созданию монументального труда по истории Пруссии, которому ученый посвятил многие годы исследований. Послевоенные годы стали весьма плодотворными в плане научных изысканий. Во второй половине 1940-х – начале 1950-х гг. Владимир Николаевич опубликовал несколько фундаментальных статей, посвященных истории Пруссии. В них историк изложил основные итоги своих исследований, которые к тому времени были оформлены в рукопись монументальной монографии по истории Древней Пруссии. Однако в Москве рукопись получила отрицательную рецензию: ученого обвиняли в том, что он «методологических указаний И. В. Сталина не усвоил, не понял». Черновик монографии сохранился в личном деле академика Перцева в архиве НАН Беларуси и, несомненно, заслуживает публикации. На протяжении 1950-х гг. Владимиром Николаевичем в минских издательствах были опубликованы две книги по истории Германии Нового времени. В качестве академика-секретаря Отделения общественных наук АН БССР он включился в подготовку «матрицы», образца прочтения прошлого белорусов. Под его редакцией в 1946 г. вышел первый выпуск «Истории БССР», в котором рассмотрено развитие белорусских земель до XIII в. Издание было встречено ожесточенной критикой методологических позиций. После ряда согласований и обсуждений, в том числе на партийно-правительственном уровне, первый том «Истории Белорусской ССР» увидел свет в 1954 г., второй – в 1958 г. В 1953 г. В. Н. Перцев выступил редактором последнего тома сборника документов по истории Беларуси, завершив дело, начатое коллегами-предшественниками – В. К. Щербаковым и Н. М. Никольским. Обращение к проблемам белорусской истории, которые ранее не были в центре научных интересов академика, вынудили его глубже познакомиться с рядом спорных и дискуссионных вопросов. Перцев разрабатывал темы формирования белорусской народности, распространения гуманистических взглядов на белорусских землях, деятельности просветителей, в первую очередь Ф. Скорины. Он достаточно смело и по-научному убедительно рассмотрел, и оценил роль Кастуся Калиновского, всячески содействовал появлению новых исследований о восстании 1863–1864 гг. и его руководителе.</p>', 2017, 1);				
INSERT INTO "MemoryPlaces" (id,ordinal_number,name,description,coordinates_id,scientist_id) VALUES (18, 18, 'Исторический факультет БГУ', '<p>Владимир Николаевич вернулся в Минск. Здесь он с новым творческим подъемом взялся за перспективную, малоизученную проблематику: историю белорусской культуры, историографию белорусской исторической науки, историю революционного движения в Белоруссии, а затем и историю Западной Белоруссии. Преподавательская деятельность В. Н. Перцева продолжилась на историческом факультете БГУ, на кафедре пединститута, которую он продолжил возглавлять. Правда, в 1947 г. из-за большой научной и административной нагрузки ее пришлось оставить. В 1955 г. В. Н. Перцев стал заведующим объединённой кафедрой – после слияния кафедры истории Древнего мира и кафедры истории Средних веков в одну. Для студенческой аудитории В. Н. Перцев разработал столь необходимые в повседневной жизни вуза лекции на темы: «Форма и методы самостоятельной работы» и «Как работать с книгой». Лекция о формах и методах самостоятельной работы студентов интересна тем, что раскрывает отношение ученого к самостоятельной научно-исследовательской работе студентов гуманитарных факультетов. Он подчеркивал необходимость обучения студентов творческому самообразованию, самостоятельному научному мышлению на лекциях, практических и семинарских занятиях, при написании курсовых и дипломных работ и занятиях в научно-исследовательских кружках. Будучи уже весьма преклонного возраста, Владимир Николаевич редко появлялся на факультете, и заседания кафедры проходили чаще всего у него дома. Однако он выдерживал темп работы в БГУ и в Академии наук. Но когда в 1959 г. было запрещено совмещение работы на полную ставку в двух этих учреждениях, профессор и академик встал перед выбором, от какой должности отказаться. И предпочел сохранить заведование кафедрой, уйдя из Академии наук.</p>', 2018, 1);				
INSERT INTO "MemoryPlaces" (id,ordinal_number,name,description,coordinates_id,scientist_id) VALUES (19, 19, 'г. Сан-Франциско', '<p>Пожалуй, одним из наиболее ярких моментов жизни Владимира Николаевича можно считать участие в работе правительственной делегации Белорусской ССР на конференции Организации Объединенных Наций в Сан-Франциско в мае-июне 1945 г. Академик-историк, профессор БГУ был одним из делегатов – учредителей ООН от БССР, его подпись стоит под документом о создании ООН. <br/>Из стенограммы заседания Президиума Верховного Совета БССР по вопросу о ратификации устава Организации объединённых наций: «[Киселёв:] Товарищ Сталин и товарищ Молотов на совещании в Ялте поставили вопрос о приглашении на будущую конференцию в Сан-Франциско Украинской и Белорусской Республик, поскольку народы этих республик сделали большой вклад в дело разгрома немецкого фашизма. Получив такое приглашение, правительство Белорусской республики сформировало делегацию в составе 5 человек, в которую входили: я — возглавлял эту делегацию, тов. Беляков — народный комиссар промышленности стройматериалов, академики Жебрак, Перцев, тов. Шмыгов.<br/>Устав Организации Объединенных Наций вместе со Статутом Международного Суда, составляющим неотъемлемую часть Устава, был единогласно принят в конце конференции 25 июня 1945 г. в Оперном театре Сан-Франциско и подписан на следующий день в зале театра Хербста в Мемориальном здании ветеранов войны. Он вступил в силу в соответствии с пунктом 3 статьи 110 24 октября 1945 г. после сдачи на хранение ратификационных грамот пятью постоянными членами Совета Безопасности и большинством всех других подписавших его сторон. <br/>Во время пребывания в Америке Владимир Николаевич провел ряд встреч с представителями научных и политических кругов США, договариваясь о научных контактах и обмене литературой. <br/>Фильм о подписании договора  https://www.unmultimedia.org/avlibrary/asset/1288/1288630/<br/>Как был подписан Устав ООН/ Архивное аудио https://news.un.org/ru/audio/2017/10/1314922<br/>Британский актер сэр Лоуренс Оливье читает Преамбулу к Уставу Организации Объединенных Наций https://soundcloud.com/united-nations-eng/reading-of-the-preamble-to-the-un-charter-by-sir-laurence-olivier?utm_source=clipboard&amp;utm_campaign=wtshare&amp;utm_medium=widget&amp;utm_content=https%253A%252F%252Fsoundcloud.com%252Funited-nations-eng%252Freading-of-the-preamble-to-the-un-charter-by-sir-laurence-olivier<br/>После 1945 г. Владимир Николаевич Перцев принимал участие в ряде международных съездов и конгрессов, включая I Общеславянский конгресс ученых-славяноведов в марте 1948 г. в Москве и II Всесоюзный конгресс сторонников мира.</p>', 2019, 1);				
INSERT INTO "MemoryPlaces" (id,ordinal_number,name,description,coordinates_id,scientist_id) VALUES (20, 20, 'Депутат Верховного Совета БССР', '<p>Многие годы (с 1947 г. по 1960 г.) Владимир Николаевич избирался депутатом Верховного Совета БССР по Березинскому избирательному округу. Данную обязанность ученый воспринимал весьма ответственно. Лучше всего об этом свидетельствует следующий факт. В 1953 г. к нему обратились жители д. Высокая Гора с просьбой оказать помощь в строительстве в деревне школы. Взяв просьбу на контроль, депутат через некоторое время убедился, что работы, хоть и запланированы, фактически не ведутся. И тогда он решил внести на строительство школы свои личные сбережения, благодаря чему оно было завершено.</p>', 2020, 1);			
INSERT INTO "MemoryPlaces" (id,ordinal_number,name,description,coordinates_id,scientist_id) VALUES (21, 21, 'г. Минск', '<p>Научная и общественная деятельность Владимира Николаевича Перцева отмечена рядом государственных наград. Он кавалер трех орденов Ленина, ордена Трудового Красного Знамени, награжден медалью «За доблестный труд в Великой Отечественной войне», еще во время войны ему было присуждено звание заслуженного деятеля науки БССР. В связи со своим 70-летием он писал в 1947 г.: «В течение всей своей научной деятельности я стремился придать моей научной и педагогической работе просветительский, это значит не цеховой, не узкокабинетный, а полезный для общества, широких масс характер». В Беларуси В. Н. Перцев опубликовал около 30 своих работ, в которых исследуются проблемы по истории античного общества и ряда европейских стран: Англии, Германии, Италии, Франции. Владимир Николаевич Перцев умер 3 июня 1960 г. в возрасте 82 лет. Память о нем увековечена мемориальной доской, установленном в Минске на доме, где прошли его последние годы. С 1995 каждый нечетный год в честь В. Н. Перцева и Н. М. Никольского проводится конференция «Лістападаўскія чытанні». Многие ученики профессора Перцева плодотворно работают сейчас в вузах страны, в средних школах.</p>', 2021, 1);				
INSERT INTO "MemoryPlaces" (id,ordinal_number,name,description,coordinates_id,scientist_id) VALUES (22, 22, 'Петербургский технологический институт (сегодня Санкт-Петербургский практический технологический институт)', '<p>Владимир Николаевич предпринимал попытки учиться в Петербургском технологическом институте.</p>', 2022, 1);				
INSERT INTO "MemoryPlaces" (id,ordinal_number,name,description,coordinates_id,scientist_id) VALUES (23, 23, 'г. Варшава', '<p>Непродолжительное время учился в Варшавском ветеринарном институте.</p>', 2023, 1);				
INSERT INTO "MemoryPlaces" (id,ordinal_number,name,description,coordinates_id,scientist_id) VALUES (24, 24, 'г. Вена',  '<p>После второго исключения Владимир Перцев предпринял попытку продолжить обучение за границей, слушал лекции в Венском университете.</p>', 2024, 1);				
INSERT INTO "MemoryPlaces" (id,ordinal_number,name,description,coordinates_id,scientist_id) VALUES (25, 25, 'г. Берлин', '<p>После Венского университета Владимир Николаевич некоторое время учился в Берлинском университете, но недостаток средств вынудил вернуться в Россию.</p>', 2025, 1);				
INSERT INTO "MemoryPlaces" (id,ordinal_number,name,description,coordinates_id,scientist_id) VALUES (26, 26, 'г. Витебск', '<p>В период 1918-1920 года перцев преподавал в Витебском институте народного образования.</p>', 2026, 1);				
INSERT INTO "MemoryPlaces" (id,ordinal_number,name,description,coordinates_id,scientist_id) VALUES (27, 27, 'г. Орша', '<p>В период 1918-1920 года Владимир Николаевич преподавал в партийной школе при Оршанском пролетарском университете.</p>', 2027, 1);				
INSERT INTO "MemoryPlaces" (id,ordinal_number,name,description,coordinates_id,scientist_id) VALUES (28, 28, 'г. Городок', '<p>Также, в 1918-1920 гг., Владимир Николаевич преподавал на курсах для школьных работников в местечке Городок Витебской губернии.</p>', 2028, 1);				
INSERT INTO "MemoryPlaces" (id,ordinal_number,name,description,coordinates_id,scientist_id) VALUES (29, 29, 'Квартира В. Н. Перцева', '<p>Здесь в квартире №2 по улице Совецкая 19 проживал Владимир Николаевич с семьей.</p>', 2029, 1);				
